<?phpnamespace App\Service;class ImportService{    /**     * This function returns an array of subjects and their corresponding subject codes     */    public const SUBJECTS = [        'Angewandte Mathematik' => ['MATHEMATIK'],        'Deutsch' => ['DEUTSCH', 'DEUTSCH-LITERATURKUNDE', 'DEUTSCH-SPRACHLEHRE', 'DEUTSCH-SPRACHLEHRE-ZUSATZ'],        'Englisch' => ['ENGLISCH', 'ENGLISCH-SPRACHLEHRE', 'ENGLISCH-WÖRTERBÜCHER', 'ENGLISCH-ZUSATZ'],        'Ethik' => ['ETHIK'],        'Fachlehre IT-Elektronik' => ['ELEKTRONIK', 'SYSTEMTECHNIK', 'INFORMATIK'],        'Fachlehre Maschienenbau' => ['MASCHINENBAU', 'FERTIGUNGSTECHNIK', 'MASCHINENBAU-ZUSATZ', 'TECHNISCHES ZEICHNEN', 'MOTORENTECHNIK', 'ANTRIEBSTECHNIK'],        'Fachlehre Mechatronik' => ['MECHATRONIK', 'GEOMETRIE'],        'Geografie, Geschichte, Politische Bildung' => ['GEOGRAFIE', 'GESCHICHTE', 'POLITISCHE BILDUNG'],        'Naturwissenschaften' => ['BIOCHEMIE', 'CHEMIE', 'PHYSIK', 'NATURWISSENSCHAFTEN'],        'Wirtschaft' => ['WIRTSCHAFT', 'BETRIEBSWIRTSCHAFT', 'WIRTSCHAFTSRECHT'],    ];    /**     *  This array contains the user and shortname for each subject     */    public const USERS = [        'Angewandte Mathematik' => ['user' => "nieb", "shortname" => "AM"],        'Deutsch' => ['user' => "proe", "shortname" => "D"],        'Englisch' => ['user' => "hesd", "shortname" => "E"],        'Ethik' => ['user' => "pfas", "shortname" => "ETH"],        'Fachlehre IT-Elektronik' => ['user' => "pusc", "shortname" => ""],        'Fachlehre Maschienenbau' => ['user' => "obea", "shortname" => ""],        'Fachlehre Mechatronik' => ['user' => "hint", "shortname" => ""],        'Geografie, Geschichte, Politische Bildung' => ['user' => "amot", "shortname" => "GGP"],        'Naturwissenschaften' => ['user' => "kimc", "shortname" => "NW2"],        'Wirtschaft' => ['user' => "hils", "shortname" => "WIR"],    ];    /**     * This function checks if the given subject is valid and returns the subject's name     * @param $subject     * @return string|null     */    public function checkSubject($subject): ?string    {        foreach (self::SUBJECTS as $key => $value) {            if (in_array(strtoupper($subject), $value, true)) {                return $key;            }        }        return null;    }    /**     * This function returns the head of subject and shortname for the given subject     * @param $subject     * @return array|null     */    public function getUser($subject): ?array    {        foreach (self::USERS as $key => $value) {            if ($key === $subject) {                return $value;            }        }        return null;    }}